(()=>{"use strict";(()=>{const t=document.getElementById("timer-hours"),e=document.getElementById("timer-minutes"),n=document.getElementById("timer-seconds"),o=()=>{let s=(t=>{let e=(new Date("24 april 2022").getTime()-(new Date).getTime())/1e3;return Math.floor(e/60/60/24),{timeRemaining:e,hours:Math.floor(e/60/60%24),minutes:Math.floor(e/60%60),seconds:Math.floor(e%60)}})();s.hours>10?t.textContent=s.hours:t.textContent="0"+s.hours,s.minutes>10?e.textContent=s.minutes:e.textContent="0"+s.minutes,s.seconds>10?n.textContent=s.seconds:n.textContent="0"+s.seconds,s.timeRemaining>0?setInterval(o,1e3):(t.textContent="00",e.textContent="00",n.textContent="00")};o()})()})();